import json
import base64

DEFAULT_HTTP_TIMEOUT = 10

class BaseProxy:
    def __init__(self, service_url=None, service_port=None, btc_conf_file=None, timeout=DEFAULT_HTTP_TIMEOUT, connection=None):
        self.__conn = connection
        self.__service_url = service_url
        self.__url = urlparse(service_url)
        self.__id_count = 0
        self.__auth_header = None

        if btc_conf_file:
            with open(btc_conf_file, 'r') as f:
                lines = f.readlines()
            username = None
            password = None
            for line in lines:
                line = line.strip()
                if line.startswith('rpcuser='):
                    username = line.split('=', 1)[1]
                elif line.startswith('rpcpassword='):
                    password = line.split('=', 1)[1]
            if username and password:
                self.__auth_header = base64.b64encode(f"{username}:{password}".encode()).decode('utf-8')
            else:
                raise ValueError("Missing rpcuser or rpcpassword in config file")

        if not connection:
            if self.__url.scheme == 'http':
                self.__conn = httplib.HTTPConnection(self.__url.hostname, self.__url.port or 80, timeout=timeout)
            else:
                self.__conn = httplib.HTTPSConnection(self.__url.hostname, self.__url.port or 443, timeout=timeout)

    def _call(self, service_name, *args):
        request_id = self.__id_count
        self.__id_count += 1
        payload = {
            "jsonrpc": "2.0",
            "method": service_name,
            "params": args,
            "id": request_id
        }
        headers = {"Content-Type": "application/json"}
        if self.__auth_header:
            headers["Authorization"] = "Basic " + self.__auth_header
        self.__conn.request("POST", self.__url.path, body=json.dumps(payload), headers=headers)
        return self._get_response()

    def _get_response(self):
        response = self.__conn.getresponse()
        data = response.read().decode('utf-8')
        try:
            json_response = json.loads(data)
        except json.JSONDecodeError:
            raise Exception("Invalid JSON response")
        if 'error' in json_response and json_response['error'] is not None:
            raise Exception(json_response['error']['message'])
        return json_response.get('result')

    def close(self):
        if self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()

