def _probe(filename, cmd='ffprobe', timeout=None, **kwargs):
    args = [cmd, '-v', 'quiet', '-print_format', 'json', '-show_format', '-show_streams']
    for k, v in kwargs.items():
        args.append(k)
        args.append(v)
    args.append(filename)
    proc = subprocess.Popen(args, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
    try:
        stdout, stderr = proc.communicate(timeout=timeout)
    except subprocess.TimeoutExpired:
        proc.kill()
        stdout, stderr = proc.communicate()
        if proc.returncode != 0:
            raise ffmpeg.Error(stderr=stderr, message=f"ffprobe failed: {stderr.decode()}")
        else:
            raise ffmpeg.Error(stderr=stderr, message="ffprobe timed out and was killed")
    if proc.returncode != 0:
        raise ffmpeg.Error(stderr=stderr, message=f"ffprobe failed: {stderr.decode()}")
    return json.loads(stdout.decode())

