from httpx import TooManyRequests

def translate_text(text, sourceLang=None, targetLang="EN", numberAlternative=0, printResult=False, proxies=None, api_key=None):
    url = "https://api.deepl.com/v2/translate"
    headers = {"Authorization": f"DeepL-Auth-Key {api_key}", "Content-Type": "application/x-www-form-urlencoded"}
    data = {"text": text, "source_lang": sourceLang or "auto", "target_lang": targetLang}
    if numberAlternative > 0:
        data["alt-trans"] = numberAlternative

    try:
        with httpx.Client(proxies=proxies) as client:
            response = client.post(url, data=data, headers=headers, timeout=10.0)
    except httpx.RequestError:
        return None

    if response.status_code == 429:
        raise TooManyRequests(response=response)

    if response.status_code != 200:
        return None

    try:
        response_json = response.json()
    except json.JSONDecodeError:
        return None

    if "translations" not in response_json or not response_json["translations"]:
        return None

    translations = response_json["translations"][0]
    if numberAlternative <= 1:
        result = translations.get("text")
        if printResult:
            print(result)
        return result
    else:
        alternatives = translations.get("alternatives", [])
        if printResult:
            print(alternatives)
        return alternatives

import socket
import struct
import base64

class socksocket(socket.socket):
    default_proxy = None

    def __init__(self, family=socket.AF_INET, type=socket.SOCK_STREAM, proto=0, *args, **kwargs):
        super().__init__(family, type, proto, *args, **kwargs)
        self.proxy = None
        self.proxy_sockname = None
        self.proxy_peername = None
        self._timeout = None

    def set_proxy(self, proxy_type, addr, port, rdns=True, username=None, password=None):
        self.proxy = (proxy_type.lower(), addr, port, rdns, username, password)

    def connect(self, dest_pair):
        if not self.proxy:
            return super().connect(dest_pair)
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        dest_host, dest_port = dest_pair
        if rdns:
            dest_addr = dest_host
        else:
            try:
                dest_ip = socket.getaddrinfo(dest_host, None)[0][4][0]
                dest_addr = dest_ip
            except socket.gaierror:
                raise socket.error(f"Failed to resolve destination host: {dest_host}")
        proxy_info = self._proxy_addr()
        self._timeout = self.gettimeout()
        self.settimeout(1.0)
        try:
            self.connect((proxy_info[0], proxy_info[1]))
        except socket.error as e:
            self.settimeout(self._timeout)
            raise
        self.settimeout(self._timeout)
        self.proxy_sockname = self.getsockname()
        self.proxy_peername = self.getpeername()
        if proxy_type == 'socks4':
            self._negotiate_SOCKS4(dest_addr, dest_port)
        elif proxy_type == 'socks5':
            self._negotiate_SOCKS5(dest_addr, dest_port)
        elif proxy_type == 'http':
            self._negotiate_HTTP(dest_host, dest_port)
        else:
            raise ValueError(f"Unsupported proxy type: {proxy_type}")

    def _readall(self, file, count):
        data = b''
        while len(data) < count:
            chunk = file.recv(count - len(data))
            if not chunk:
                break
            data += chunk
        return data

    def _proxy_addr(self):
        proxy_type, proxy_addr, proxy_port, rdns, username, password = self.proxy
        return (proxy_addr, proxy_port)

    def _negotiate_SOCKS4(self, dest_addr, dest_port):
        username = self.proxy[4] or ''
        dest_ip = socket.inet_aton(dest_addr)
        packet = b'\x04\x01' + struct.pack('>H', dest_port) + dest_ip + username.encode('ascii') + b'\x00'
        self.sendall(packet)
        response = self._readall(self, 8)
        if len(response) < 8:
            raise socket.error("SOCKS4 negotiation failed: incomplete response")
        ver, rep, port, ip = struct.unpack('>BBH4s', response)
        if ver != 4:
            raise socket.error("Invalid SOCKS4 version")
        if rep != 0:
            raise socket.error(f"SOCKS4 error: {rep}")
        self.proxy_peername = (socket.inet_ntoa(ip), port)

    def _negotiate_SOCKS5(self, dest_addr, dest_port):
        self.sendall(b'\x05\x01\x00')
        response = self._readall(self, 2)
        if len(response) < 2:
            raise socket.error("SOCKS5 negotiation failed")
        ver, method = struct.unpack('!BB', response)
        if ver != 5:
            raise socket.error("Invalid SOCKS5 version")
        if method != 0:
            if method != 2:
                raise socket.error(f"Unsupported authentication method: {method}")
            username = self.proxy[4] or ''
            password = self.proxy[5] or ''
            auth_packet = b'\x01' + chr(len(username)).encode('ascii') + username.encode('ascii') + chr(len(password)).encode('ascii') + password.encode('ascii')
            self.sendall(auth_packet)
            auth_response = self._readall(self, 2)
            if len(auth_response) < 2 or auth_response[1] != 0:
                raise socket.error("Authentication failed")
        packet = b'\x05\x01\x00'
        if isinstance(dest_addr, str):
            packet += b'\x03' + len(dest_addr).to_bytes(1, 'big') + dest_addr.encode('ascii')
        else:
            if socket.inet_pton(socket.AF_INET, dest_addr):
                packet += b'\x01' + socket.inet_aton(dest_addr)
            else:
                packet += b'\x04' + socket.inet_pton(socket.AF_INET6, dest_addr)
        packet += struct.pack('>H', dest_port)
        self.sendall(packet)
        response = self._readall(self, 4)
        if len(response) < 4:
            raise socket.error("SOCKS5 negotiation failed")
        ver, cmd, rep, atyp = struct.unpack('!BBBB', response)
        if ver != 5 or rep != 0:
            raise socket.error(f"SOCKS5 error: {rep}")
        if atyp == 1:
            address = self._readall(self, 4)
            port = struct.unpack('>H', self._readall(self, 2))[0]
        elif atyp == 3:
            length = ord(self._readall(self, 1))
            address = self._readall(self, length)
            port = struct.unpack('>H', self._readall(self, 2))[0]
        elif atyp == 4:
            address = self._readall(self, 16)
            port = struct.unpack('>H', self._readall(self, 2))[0]
        else:
            raise socket.error("Unknown address type")
        self.proxy_peername = (socket.inet_ntoa(address) if atyp == 1 else address.decode('ascii'), port)

    def _negotiate_HTTP(self, dest_host, dest_port):
        host = f"{dest_host}:{dest_port}"
        request = f"CONNECT {host} HTTP/1.1\r\nHost: {host}\r\nConnection: close\r\n\r\n"
        self.sendall(request.encode('ascii'))
        response = self._readall(self, 1024)
        if not response.startswith(b'HTTP/1.1 200'):
            if b'407 Proxy Authentication Required' in response:
                auth = base64.b64encode(f"{self.proxy[4]}:{self.proxy[5]}".encode('ascii')).decode('ascii')
                auth_request = f"CONNECT {host} HTTP/1.1\r\nHost: {host}\r\nProxy-Authorization: Basic {auth}\r\nConnection: close\r\n\r\n"
                self.sendall(auth_request.encode('ascii'))
                response = self._readall(self, 1024)
                if not response.startswith(b'HTTP/1.1 200'):
                    raise socket.error("HTTP proxy authentication failed")
            else:
                raise socket.error(f"HTTP proxy error: {response}")
        self.proxy_peername = (dest_host, dest_port)

