import socket
import threading
from typing import List, Dict, Any, Optional

class Client:
    def __init__(self, host: str = "127.0.0.1", port: int = 8080):
        self.host = host
        self.port = port
        self.pending_commands: List[Any] = []
        self.socket: Optional[socket.socket] = None
        self.client_id: Optional[str] = None
        self.current_custom_attributes: Dict[str, Any] = {}
        self.clients_attributes: Dict[str, Dict[str, Any]] = {}
        self.rooms_attributes: Dict[str, Dict[str, Any]] = {}
        self.current_room: Optional[str] = None
        self._stop_event = threading.Event()
        self._recv_thread = None

    def connect(self):
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.host, self.port))
        self._recv_thread = threading.Thread(target=self._receive_loop)
        self._recv_thread.start()

    def disconnect(self):
        if self.socket:
            self._stop_event.set()
            self.socket.close()
            self.socket = None
            if self._recv_thread:
                self._recv_thread.join()

    def is_connected(self) -> bool:
        return self.socket is not None and self.socket.fileno() != -1

    def send_command(self, command: Any) -> bool:
        if not self.is_connected():
            return False
        self.pending_commands.append(command)
        return True

    def handle_connection_lost(self):
        self.disconnect()
        self.client_id = None
        self.current_room = None
        self.current_custom_attributes = {}
        self.clients_attributes = {}
        self.rooms_attributes = {}

    def _receive_loop(self):
        while not self._stop_event.is_set():
            try:
                data = self.socket.recv(4096)
                if not data:
                    self.handle_connection_lost()
                    break
                self._process_data(data)
            except Exception:
                self.handle_connection_lost()
                break

    def _process_data(self, data: bytes):
        lines = data.decode().split('\n')
        for line in lines:
            if line.strip():
                self._handle_command(line)

    def _handle_command(self, command: str):
        if command.startswith("ID:"):
            self.client_id = command.split(":", 1)[1]
        elif command.startswith("ROOM:"):
            self.current_room = command.split(":", 1)[1]
        elif command.startswith("ATTR:"):
            key, value = command.split(":", 2)[1:]
            self.current_custom_attributes[key] = value
        elif command.startswith("CLIENTS:"):
            self.clients_attributes = eval(command.split(":", 1)[1])
        elif command.startswith("ROOMS:"):
            self.rooms_attributes = eval(command.split(":", 1)[1])

    def fetch_outgoing_commands(self, commands_send_interval: int = 0):
        if not self.is_connected() or not self.pending_commands:
            return
        for cmd in self.pending_commands:
            self.socket.sendall(f"{cmd}\n".encode())
        self.pending_commands.clear()

    def fetch_incoming_commands(self) -> List[str]:
        return []

    def fetch_commands(self, commands_send_interval: int = 0) -> List[str]:
        self.fetch_outgoing_commands(commands_send_interval)
        return self.fetch_incoming_commands()

    def __del__(self):
        self.disconnect()

