        raw = self.get_raw_stats(host, port)
        stats = {}
        for line in raw.splitlines():
            if line.startswith('STAT '):
                parts = line.split()
                if len(parts) < 3:
                    continue
                key = parts[1]
                value_str = parts[2]
                try:
                    value = int(value_str)
                except ValueError:
                    try:
                        value = float(value_str)
                    except ValueError:
                        value = value_str
                stats[key] = value
        return stats

