import struct

class RConError(Exception):
    pass

class RConConnection:
    def __init__(self, target: str, port: int) -> None:
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect((target, port))
        self.rid = 0

    def send(self, t: int, payload: str) -> bytes:
        length = 4 + 1 + 2 + len(payload) + 1
        packet = struct.pack('>I', length) + struct.pack('B', t) + struct.pack('>H', self.rid) + payload.encode('ascii') + b'\x00'
        self.sock.sendall(packet)

        length_data = self.sock.recv(4)
        if not length_data:
            raise RConError("Connection closed")
        length = struct.unpack('>I', length_data)[0]
        remaining = length - 4
        response_data = b''
        while remaining > 0:
            chunk = self.sock.recv(remaining)
            if not chunk:
                raise RConError("Connection closed")
            response_data += chunk
            remaining -= len(chunk)

        if len(response_data) < 3:
            raise RConError("Incomplete response")
        response_type = response_data[0]
        response_rid = struct.unpack('>H', response_data[1:3])[0]
        if response_rid != self.rid:
            raise RConError(f"Response RID mismatch: expected {self.rid}, got {response_rid}")
        payload_part = response_data[3:-1]
        if response_type == 0:
            self.rid += 1
            return payload_part
        elif response_type == 1:
            raise RConError(f"RCon error: {payload_part.decode('ascii')}")
        else:
            raise RConError(f"Unknown response type {response_type}")

    def login(self, password: str) -> None:
        self.send(3, password)

    def command(self, com: str, args: str) -> None:
        self.send(2, f"{com} {args}")

    def close(self) -> None:
        self.sock.close()

