class TCPForwardServer:
    def __init__(self, lhost: str, lport: int, rdev: 'Device', rport: int) -> None:
        self._server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._server.bind((lhost, lport))
        self._server.listen(5)
        self.input_list = [self._server]
        self.channel = {}
        self._rdev = rdev
        self._rport = rport
        self.BUFFER_SIZE = 4096
        self.DELAY = 0.1

    def main_loop(self) -> None:
        while True:
            readable, _, _ = select.select(self.input_list, [], [], self.DELAY)
            for sock in readable:
                if sock == self._server:
                    self.on_accept()
                else:
                    self.on_recv(sock)

    def on_accept(self) -> None:
        client_sock, addr = self._server.accept()
        dev_sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        dev_sock.connect((self._rdev.host, self._rport))
        self.input_list.append(client_sock)
        self.input_list.append(dev_sock)
        self.channel[client_sock] = dev_sock

    def on_recv(self, sock):
        if sock in self.channel:
            data = sock.recv(self.BUFFER_SIZE)
            if not data:
                self.on_close(sock)
            else:
                dev_sock = self.channel[sock]
                dev_sock.send(data)
        else:
            for client, dev in self.channel.items():
                if dev == sock:
                    data = sock.recv(self.BUFFER_SIZE)
                    if not data:
                        self.on_close(sock)
                    else:
                        client.send(data)
                    break

    def on_close(self, sock):
        if sock in self.input_list:
            self.input_list.remove(sock)
        if sock in self.channel:
            dev_sock = self.channel[sock]
            if dev_sock in self.input_list:
                self.input_list.remove(dev_sock)
            dev_sock.close()
            del self.channel[sock]
        else:
            for client, dev in self.channel.items():
                if dev == sock:
                    if client in self.input_list:
                        self.input_list.remove(client)
                    client.close()
                    del self.channel[client]
                    break

