class UDPSocketHandler:
    IP_BINDANY = socket.SO_REUSEADDR
    IP_RECVDSTADDR = socket.IP_RECVDSTADDR

    @staticmethod
    def recv_udp(listener, bufsize):
        data, ancdata, flags, addr = listener.recvmsg(bufsize, 0)
        dstip = None
        for level, optname, value in ancdata:
            if level == socket.SOL_IP and optname == UDPSocketHandler.IP_RECVDSTADDR:
                if len(value) == 6:
                    ip_bytes, port = struct.unpack('!4sH', value)
                    dstip = (socket.inet_ntoa(ip_bytes), port)
                elif len(value) == 18:
                    ip_bytes, port = struct.unpack('!16sH', value)
                    dstip = (socket.inet_ntop(socket.AF_INET6, ip_bytes), port)
        return (addr, dstip, data)

    @staticmethod
    def send_udp(sock, srcip, dstip, data):
        new_sock = socket.socket(family=sock.family, type=socket.SOCK_DGRAM)
        new_sock.bind(srcip)
        new_sock.sendto(data, dstip)
        new_sock.close()

    @staticmethod
    def setup_udp_listener(udp_listener):
        for s in [getattr(udp_listener, 'v4', None), getattr(udp_listener, 'v6', None)]:
            if s is not None:
                s.setsockopt(socket.SOL_SOCKET, UDPSocketHandler.IP_BINDANY, 1)
                if s.family == socket.AF_INET:
                    s.setsockopt(socket.SOL_IP, UDPSocketHandler.IP_RECVDSTADDR, 1)
                elif s.family == socket.AF_INET6:
                    s.setsockopt(socket.SOL_IPV6, socket.IPV6_RECVDSTADDR, 1)

