class LegacyWebSocketProvider:
    _loop: ClassVar[asyncio.AbstractEventLoop] = asyncio.get_event_loop()
    endpoint_uri: Optional[Union[str, Any]] = None
    websocket_timeout: int = 10
    conn: PersistentWebSocket

    def __init__(self, endpoint_uri: Optional[Union[str, Any]], websocket_timeout: int = 10):
        self.endpoint_uri = endpoint_uri
        self.websocket_timeout = websocket_timeout
        self.conn = PersistentWebSocket(self.endpoint_uri, timeout=websocket_timeout)

    async def coro_make_request(self, request_data: bytes) -> RPCResponse:
        try:
            await self.conn.ws.send(request_data)
            response = await asyncio.wait_for(self.conn.ws.recv(), timeout=self.websocket_timeout)
            return RPCResponse(response)
        except Exception as e:
            raise

    def make_request(self, method: str, params: Any) -> RPCResponse:
        loop = self._loop
        request_data = self._serialize_request(method, params)
