    def make_batch_request(self, requests: List[Tuple[str, Any]]) -> List[RPCResponse]:
        loop = self._loop
        tasks = [self.coro_make_request(self._serialize_request(method, params)) for method, params in requests]
        results = loop.run_until_complete(asyncio.gather(*tasks))
        return results

    def _serialize_request(self, method: str, params: Any) -> bytes:
        import json
        return json.dumps({"method": method, "params": params}).encode('utf-8')

