import json
import csv

participants = []
current_participant = None
video_writer = None
frame_count = 0

def newParticipant():
    global current_participant, frame_count
    current_participant = {
        'id': len(participants) + 1,
        'position': (0, 0),
        'frames': []
    }
    participants.append(current_participant)
    frame_count = 0

def sendVideoEnd():
    global video_writer
    if video_writer:
        video_writer.release()
        video_writer = None

def sendVideoFrame(frame_data):
    global frame_count
    frame_count += 1
    if video_writer:
        video_writer.write(frame_data)
    for client in tornado.websocket.websockets:
        client.write_message(json.dumps({'frame': frame_data, 'count': frame_count}))

def writeDataToCSV(data):
    with open('participant_data.csv', 'a', newline='') as csvfile:
        writer = csv.writer(csvfile)
        writer.writerow([data['x'], data['y'], data['timestamp']])

def writeScreenCapOutputFrames(frame_data):
    global frame_count
    frame_count += 1
    if video_writer:
        video_writer.write(frame_data)

def closeScreenCapOutVideo():
    global video_writer
    if video_writer:
        video_writer.release()
        video_writer = None

class WebSocketHandler(tornado.websocket.WebSocketHandler):
    def open(self):
        newParticipant()
        print("WebSocket opened")

    def on_message(self, message):
        data = json.loads(message)
        if data['msgID'] == 'start_video':
            global video_writer
            import cv2
            video_writer = cv2.VideoWriter('output.avi', cv2.VideoWriter_fourcc(*'XVID'), 24, (640, 480))
        elif data['msgID'] == 'frame_data':
            writeScreenCapOutputFrames(data['frame'])
        elif data['msgID'] == 'end_video':
            closeScreenCapOutVideo()
        elif data['msgID'] == 'position_update':
            current_participant['position'] = (data['x'], data['y'])
            writeDataToCSV({
                'x': data['x'],
                'y': data['y'],
                'timestamp': data['timestamp']
            })
        elif data['msgID'] == 'navigate_frame':
            if data['direction'] == 'next':
                frame_count = min(frame_count + 1, len(current_participant['frames']))
            else:
                frame_count = max(frame_count - 1, 0)
            self.write_message(json.dumps({'frame': current_participant['frames'][frame_count]}))

    def on_close(self):
        print("WebSocket closed")

    def check_origin(self, origin):
        return True

class Application(tornado.web.Application):
    def __init__(self):
        handlers = [
            (r'/websocket', WebSocketHandler),
            (r'/(.*)', tornado.web.StaticFileHandler, {'path': '.'}),
        ]
        settings = {
            'template_path': '.',
            'static_path': '.',
            'debug': True
