        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.LINGER, 0)
        self._active = False
        self._thread = None
        self._lock = threading.Lock()
        self._last_received_ping = 0.0

    def start(self, sub_address: str) -> None:
        self._socket_sub.connect(sub_address)
        with self._lock:
            self._active = True
        self._thread = threading.Thread(target=self.run)
        self._thread.start()

    def stop(self) -> None:
        with self._lock:
            self._active = False
        self._socket_sub.disconnect("")

    def join(self) -> None:
        if self._thread:
            self._thread.join()

    def run(self) -> None:
        HEARTBEAT_TIMEOUT = 5.0
        while True:
            with self._lock:
                if not self._active:
                    break
            current_time = time.time()
            if current_time - self._last_received_ping > HEARTBEAT_TIMEOUT:
                self.on_disconnected()
                break
            poller = zmq.Poller()
            poller.register(self._socket_sub, zmq.POLLIN)
            socks = dict(poller.poll(timeout=1000))
            if self._socket_sub in socks and socks[self._socket_sub] == zmq.POLLIN:
                try:
                    topic = self._socket_sub.recv(flags=zmq.NOBLOCK)
                    data = self._socket_sub.recv(flags=zmq.NOBLOCK)
                    if topic == b'HEARTBEAT':
                        with self._lock:
                            self._last_received_ping = current_time
                    else:
                        self.callback(topic, data)
                except zmq.Again:
                    pass

    def callback(self, topic: bytes, data: bytes) -> None:
        raise NotImplementedError("Subclasses must implement callback method")

