import uuid
import time

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver = self.context.socket(zmq.SUB)
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')
        self.identity = identity or uuid.uuid4().bytes
        self.request_id = 0
        self.timeout = timeout
        self.pending_requests = set()
        self.pending_results = {}
        self.sender.setsockopt(zmq.SNDTIMEO, timeout)
        self.receiver.setsockopt(zmq.RCVTIMEO, timeout)

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        request_id = self.request_id
        self.request_id += 1
        self.sender.send_multipart([self.identity, str(request_id).encode(), msg.encode()])
        self.pending_requests.add(request_id)

    def _recv(self):
        while self.pending_requests:
            try:
                parts = self.receiver.recv_multipart()
                request_id = int(parts[0])
                data = msgpack.loads(parts[1])
                self.pending_results[request_id] = data
                self.pending_requests.discard(request_id)
                return
            except zmq.Again:
                time.sleep(0.01)

    def _recv_ndarray(self):
        self._recv()
        for req_id in list(self.pending_results.keys()):
            if req_id in self.pending_results:
                arr = np.frombuffer(self.pending_results[req_id], dtype=np.float32)
                del self.pending_results[req_id]
                return arr
        return None

    def encode(self, texts, blocking=True):
        if not isinstance(texts, list):
            texts = [texts]
        results = []
        for text in texts:
            self._send(text)
            if blocking:
                while self.request_id - 1 not in self.pending_results:
                    time.sleep(0.01)
                results.append(self.pending_results.pop(self.request_id - 1))
            else:
                results.append(None)
        return results[0] if len(texts) == 1 and blocking else results

    def fetch(self, delay=0.0):
        while self.pending_requests:
            for req_id in list(self.pending_requests):
                if req_id in self.pending_results:
                    yield self.pending_results.pop(req_id)
            time.sleep(delay)

    def fetch_all(self, sort=True, concat=False):
        results = list(self.pending_results.values())
        if sort:
            items = sorted(self.pending_results.items(), key=lambda x: x[0])
            results = [x[1] for x in items]
        if concat:
            return np.concatenate(results)
        return results

