            else:
                compressed = frame.tobytes()
            self.__msg_socket.send(compressed)

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread:
            self.__thread.join()
        self.__msg_socket.close()
        self.__msg_context.term()

