import httpx
import hmac
import hashlib
import json
from typing import Dict, Any, Optional

class ExternalActionResponse:
    def __init__(self, success: bool, data: Optional[Dict[str, Any]] = None, error: Optional[str] = None):
        self.success = success
        self.data = data
        self.error = error

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(self, payload: Dict[str, Any], signature_secret: str, additional_payload_values: Dict[str, Any] = {}, additional_headers: Dict[str, str] = {}, transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)) -> ExternalActionResponse:
        combined_payload = {**payload, **additional_payload_values}
        payload_bytes = json.dumps(combined_payload).encode('utf-8')
        signature = self._encode_payload(payload_bytes, signature_secret)

        headers = {
            "Content-Type": "application/json",
            "X-Signature": signature,
            **additional_headers
        }

        try:
            async with httpx.AsyncClient(transport=transport) as client:
                response = await client.post(self.url, json=combined_payload, headers=headers)
                response.raise_for_status()
                response_data = response.json()
                return self._validate_response(response_data)
        except httpx.HTTPStatusError as e:
            return ExternalActionResponse(success=False, error=str(e))
        except httpx.NetworkError as e:
            return ExternalActionResponse(success=False, error=f"Network error: {str(e)}")
        except Exception as e:
            return ExternalActionResponse(success=False, error=f"Unexpected error: {str(e)}")

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        hmac_obj = hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256)
        return hmac_obj.hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if response.get("success", False):
            return ExternalActionResponse(success=True, data=response.get("data"))
        else:
            return ExternalActionResponse(success=False, error=response.get("error", "Unknown error"))

