from urllib.parse import urlparse
from http.client import HTTPConnection, HTTPSConnection, BaseHTTPResponse
from urllib3.util import Timeout, Retry
from urllib3.exceptions import ConnectionPoolTimeoutError

class HTTPConnectionPool:
    def __init__(self, host: str, port: Optional[int] = None, timeout: Union[Timeout, float, None] = None, maxsize: int = 1, block: bool = False, headers: Optional[Mapping[str, str]] = None, retries: Union[Retry, bool, int, None] = None, _proxy: Optional[str] = None, _proxy_headers: Optional[Mapping[str, str]] = None, **conn_kw: Any) -> None:
        self.scheme = 'http' if port == 80 else 'https' if port == 443 else 'http'
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.headers = headers
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.conn_kw = conn_kw
        self.ConnectionCls = HTTPSConnection if self.scheme == 'https' else HTTPConnection
        self.QueueCls = queue.LifoQueue
        self.pool = self.QueueCls(maxsize=maxsize)
        self.block = block

    def _new_conn(self) -> HTTPConnection:
        conn = self.ConnectionCls(host=self.host, port=self.port, **self.conn_kw)
        if self.proxy:
            conn.set_tunnel(self.host, port=self.port, headers=self.proxy_headers)
        return conn

    def _get_conn(self, timeout: Optional[float] = None) -> HTTPConnection:
        try:
            conn = self.pool.get(timeout=timeout)
        except queue.Empty:
            if self.block:
                conn = self._new_conn()
            else:
                raise ConnectionPoolTimeoutError("No connection available")
        return conn

    def _put_conn(self, conn: Optional[HTTPConnection]) -> None:
        if conn is not None:
            try:
                self.pool.put(conn, block=self.block)
            except queue.Full:
                conn.close()

    def _make_request(self, conn: HTTPConnection, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Optional[Retry] = None, timeout: Union[Timeout, float, None] = None, **response_kw: Any) -> BaseHTTPResponse:
        if headers:
            conn.request(method, url, body=body, headers=headers)
        else:
            conn.request(method, url, body=body)
        return conn.getresponse(**response_kw)

    def urlopen(self, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Optional[Retry] = None, redirect: bool = True, assert_same_host: bool = True, timeout: Union[Timeout, float, None] = None, pool_timeout: Optional[int] = None, **response_kw: Any) -> BaseHTTPResponse:
        conn = self._get_conn(timeout=pool_timeout)
        try:
            return self._make_request(conn, method, url, body, headers, retries, timeout, **response_kw)
        finally:
            self._put_conn(conn)

    def _close_pool_connections(self, pool: queue.LifoQueue) -> None:
        while not pool.empty():
            conn = pool.get_nowait()
            conn.close()

    def close(self) -> None:
        self._close_pool_connections(self.pool)
        self.pool = self.QueueCls(maxsize=0)

