import requests

class BlockchainClient:
    def __init__(self, nodes=None):
        self.nodes = set(nodes) if nodes else set()

    def register_node(self, address):
        """
        Add a new node to the list of nodes

        :param address: Address of node. Eg. 'http://192.168.0.5:5000'
        """
        parsed_url = urlparse(address)
        if parsed_url.netloc:
            self.nodes.add(parsed_url.netloc)
        elif parsed_url.path:
            self.nodes.add(parsed_url.path)
        else:
            raise ValueError('Invalid URL')

    def resolve_conflicts(self):
        """
        Resolve conflicts by replacing our chain with the longest one in the network.

        :return: Tuple of (True if chain was replaced, new chain if replaced)
        """
        new_chain = None
        max_length = 0

        for node in self.nodes:
            try:
                response = requests.get(f'http://{node}/chain')
                if response.status_code == 200:
                    data = response.json()
                    length = data['length']
                    chain = data['chain']

                    if length > max_length:
                        max_length = length
                        new_chain = chain
            except requests.exceptions.RequestException:
                continue

        return (True, new_chain) if new_chain else (False, None)

    def get_chain(self, node):
        """
        Get the blockchain from a specific node

        :param node: Node address
