import requests
import concurrent.futures

def get_dns_info(domain, record_type):
    def query_google():
        url = 'https://dns.google/resolve'
        params = {'name': domain, 'type': record_type, 'output': 'json'}
        try:
            response = requests.get(url, params=params, timeout=5)
            if response.status_code == 200:
                try:
                    return response.json()
                except ValueError:
                    return None
            else:
                return None
        except requests.exceptions.RequestException:
            return None

    def query_cloudflare():
        url = 'https://cloudflare-dns.com/dns-query'
        params = {'name': domain, 'type': record_type}
        headers = {'Accept': 'application/dns-json'}
        try:
            response = requests.get(url, params=params, headers=headers, timeout=5)
            if response.status_code == 200:
                try:
                    return response.json()
                except ValueError:
                    return None
            else:
                return None
        except requests.exceptions.RequestException:
            return None

    with concurrent.futures.ThreadPoolExecutor() as executor:
        google_future = executor.submit(query_google)
        cloudflare_future = executor.submit(query_cloudflare)
        google_result = google_future.result()
        cloudflare_result = cloudflare_future.result()

    return [google_result, cloudflare_result]

