    def _new_conn(self):
        conn = self.ConnectionCls(host=self.host, port=self.port, **self.conn_kw)
        return conn

    def _get_conn(self, timeout=None):
        if not self.pool.empty():
            return self.pool.get()
        elif self.block:
            return self.pool.get(timeout=timeout)
        else:
            return self._new_conn()

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=False)
        except Exception:
            pass

    def _make_request(self, conn, method, url, timeout=object(), **httplib_request_kw):
        try:
            conn.request(method, url, **httplib_request_kw)
            return conn.getresponse()
        except socket.timeout:
            raise

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, timeout=object(), **response_kw):
        if retries is None:
            retries = self.retries
        conn = self._get_conn(timeout=timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers)
