import requests

class AmazonSSO:
    def __init__(self, client_id: str, client_secret: str, user_pool_id: str, region: str, access_token: str = None, refresh_token: str = None):
        self.client_id = client_id
        self.client_secret = client_secret
        self.user_pool_id = user_pool_id
        self.region = region
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.user_info = {}

    def get_new_token(self) -> str:
        token_url = f"https://{self.user_pool_id}.auth.{self.region}.amazoncognito.com/oauth2/token"
        data = {
            'grant_type': 'refresh_token',
            'client_id': self.client_id,
            'client_secret': self.client_secret,
            'refresh_token': self.refresh_token
        }
        response = requests.post(token_url, data=data)
        if response.status_code == 200:
            tokens = response.json()
            self.access_token = tokens['access_token']
            self.refresh_token = tokens.get('refresh_token', self.refresh_token)
            return self.access_token
        return None

    def get_user_info(self) -> dict:
        user_info_url = f"https://{self.user_pool_id}.auth.{self.region}.amazoncognito.com/oauth2/userInfo"
        headers = {'Authorization': f'Bearer {self.access_token}'}
        response = requests.get(user_info_url, headers=headers)
        if response.status_code == 200:
            self.user_info = response.json()
            return self.user_info
        return {}

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None, client_id: str = None, client_secret: str = None, user_pool_id: str = None, region: str = None) -> tuple:
        token_url = f"https://{user_pool_id}.auth.{region}.amazoncognito.com/oauth2/token"
        data = {
            'grant_type': 'authorization_code',
            'client_id': client_id,
            'client_secret': client_secret,
            'code': code,
            'redirect_uri': redirect_uri
        }
        response = requests.post(token_url, data=data)
        if response.status_code == 200:
            tokens = response.json()
            access_token = tokens['access_token']
            refresh_token = tokens.get('refresh_token')
            return (AmazonSSO(client_id, client_secret, user_pool_id, region, access_token, refresh_token), None)
        return (None, None)

