def upload_image(image_tensor, api_key, service='smms'):
    if not isinstance(image_tensor, torch.Tensor):
        return None

    pil_image = ToPILImage()(image_tensor)
    byte_arr = io.BytesIO()
    pil_image.save(byte_arr, format='PNG')
    image_bytes = byte_arr.getvalue()

    if service == 'smms':
        encoded_image = base64.b64encode(image_bytes).decode('utf-8')
        url = 'https://sm.ms/api/upload'
        params = {'key': api_key}
        data = {'image': encoded_image}
        try:
            response = requests.post(url, params=params, json=data)
        except:
            return None
        if response.status_code == 200:
            try:
                json_resp = response.json()
                if json_resp.get('success'):
                    return json_resp['url']
                else:
                    return json_resp.get('message', 'Upload failed')
            except:
                return None
        else:
            return 'HTTP error'
    elif service == 'imgbb':
        url = 'https://api.imgbb.com/1/upload'
        params = {'key': api_key}
        files = {'image': ('image.png', image_bytes, 'image/png')}
        try:
            response = requests.post(url, params=params, files=files)
        except:
            return None
        if response.status_code == 200:
            try:
                json_resp = response.json()
                if json_resp.get('success'):
                    return json_resp['data']['url']
                else:
                    return json_resp.get('error', 'Upload failed')
            except:
                return None
        else:
