from logging import info, debug, warning

class HurricaneElectricAPI:
    def __init__(self, token="password", proxy=None):
        self.SITE = "dyn.dns.he.net"
        self.METHOD = "POST"
        self.ACTION = "nic/update"
        self.TOKEN_PARAM = "password"
        self.TOKEN = token
        self.PROXY = proxy

    def _request(self, param=None, **params):
        if param:
            params.update(param)

        params.update({self.TOKEN_PARAM: '***'})
        info("%s/%s : %s", self.SITE, self.ACTION, params)
        params[self.TOKEN_PARAM] = self.TOKEN

        if self.PROXY:
            conn = HTTPSConnection(self.PROXY)
            conn.set_tunnel(self.SITE, 443)
        else:
            conn = HTTPSConnection(self.SITE)

        conn.request(self.METHOD, '/' + self.ACTION, urlencode(params), {
            "Content-type": "application/x-www-form-urlencoded"
        })
        response = conn.getresponse()
        res = response.read().decode('utf8')
        conn.close()

        if response.status < 200 or response.status >= 300:
            warning('%s : error[%d]:%s', self.ACTION, response.status, res)
            raise Exception(res)
        else:
            debug('%s : result:%s', self.ACTION, res)
            if not res:
                raise Exception("empty response")
            elif res[:5] == "nochg" or res[:4] == "good":
                return res
            else:
                raise Exception(res)

    def update_record(self, domain, value, record_type="A"):
        info(">>>>>%s(%s)", domain, record_type)
        res = self._request(hostname=domain, myip=value)
        if res[:4] == "good":
            result = "Record updated. New IP is: " + res[5:-1]
        elif res[:5] == "nochg":
            result = "IP not changed. IP is: " + res[6:-1]
        else:
            result = "Record update failed."
        return result

