        raise ValueError(f'No data found for symbol {symbol}')

    quote_data = quote_response['result'][0]

    result = {
        'symbol': symbol,
        'name': quote_data.get('shortName'),
        'price': quote_data.get('regularMarketPrice'),
        'volume': quote_data.get('regularMarketVolume'),
        'change': quote_data.get('regularMarketChange'),
        'percent_change': quote_data.get('regularMarketChangePercent'),
        'exchange': quote_data.get('exchange'),
        'market_cap': quote_data.get('marketCap'),
        'year_high': quote_data.get('fiftyTwoWeekHigh'),
        'year_low': quote_data.get('fiftyTwoWeekLow'),
    }

