            self.headers['Authorization'] = f'Bearer {self.api_token}'

    def submit_sync(self, request_data: Dict[str, Any]) -> Dict[str, Any]:
        response = requests.post(f"{self.base_url}/crawl", headers=self.headers, json=request_data)
        response.raise_for_status()
        return response.json()

    def submit_and_wait(self, request_data: Dict[str, Any], timeout: int = 300) -> Dict[str, Any]:
        response = requests.post(f"{self.base_url}/async/crawl", headers=self.headers, json=request_data)
        response.raise_for_status()
        task_id = response.json().get('task_id')
        if not task_id:
            raise ValueError("Task submission failed: No task_id received")

        start_time = time.time()
        while (time.time() - start_time) < timeout:
            status_response = requests.get(f"{self.base_url}/task/{task_id}/status", headers=self.headers)
            status_response.raise_for_status()
            data = status_response.json()

            if data.get('status') == 'completed':
                return data.get('result', {})

            time.sleep(1)

        raise TimeoutError(f"Task timed out after {timeout} seconds")

