class HTTPConnectionPool:
    def __init__(self, host, port=None, scheme="http", ConnectionCls=HTTPConnection, timeout=None, retries=None, proxy=None, proxy_headers=None, **conn_kw):
        self.scheme = scheme
        self.ConnectionCls = ConnectionCls
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = proxy
        self.proxy_headers = proxy_headers or {}
        self.conn_kw = conn_kw
        if self.port is None:
            self.port = 443 if self.scheme == "https" else 80

    def _new_conn(self):
        if self.proxy:
            from urllib.parse import urlparse
            proxy_parsed = urlparse(self.proxy)
            proxy_host = proxy_parsed.hostname
            proxy_port = proxy_parsed.port or 8080
            conn = self.ConnectionCls(proxy_host, proxy_port, timeout=self.timeout.connect if hasattr(self.timeout, "connect") else self.timeout, **self.conn_kw)
            if self.scheme == "https":
                conn.set_tunnel(self.host, self.port, self.proxy_headers)
        else:
            conn = self.ConnectionCls(self.host, self.port, timeout=self.timeout.connect if hasattr(self.timeout, "connect") else self.timeout, **self.conn_kw)
        return conn

    def _make_request(self, conn, method, url, timeout=_Default, chunked=False, **httplib_request_kw):
        try:
            if timeout is not _Default:
                conn.sock.settimeout(timeout)
            conn.request(method, url, **httplib_request_kw)
            return conn.getresponse()
        except socket.timeout as e:
            raise TimeoutError from e
        except Exception as e:
            raise

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        if assert_same_host:
            from urllib.parse import urlparse
            parsed_url = urlparse(url)
            if parsed_url.hostname != self.host:
                raise ValueError("URL host does not match pool host")
        retries = retries.total if hasattr(retries, "total") else retries if retries is not None else self.retries
        redirect_location = None
        while retries >= 0:
            conn = self._new_conn()
            try:
                resp = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, body=body, headers=headers or {})
                if redirect and resp.status in (301, 302, 303, 307, 308):
                    redirect_location = resp.getheader("Location")
                    if redirect_location:
                        url = redirect_location
                        retries -= 1
                        continue
                return resp
            except (TimeoutError, socket.error) as e:
                retries -= 1
                if retries < 0:
                    raise
            finally:
                if release_conn:
                    pass
                else:
                    conn.close()

