from dataclasses import dataclass

# Assume these are imported from lebot package
class LeRobotDataset:
    def __init__(self, repo_id: str):
        self.repo_id = repo_id
        self.episodes = [0, 1, 2]  # Mock data
        self.version = "1.0"
        self.namespace, self.name = repo_id.split("/")

    def get_video_path(self, episode_id: int) -> str:
        return f"/videos/{self.name}_{episode_id}.mp4"

    def get_task_description(self, episode_id: int) -> str:
        return f"Task description for episode {episode_id}"

    def get_metadata(self, episode_id: int) -> dict:
        return {"steps": 100, "duration": "30s"}

def run_server(
    dataset: Union[LeRobotDataset, None] = None,
    episodes: Optional[List[int]] = None,
    host: str = "0.0.0.0",
    port: str = "5000",
    static_folder: Path = Path(__file__).parent / "static",
    template_folder: Path = Path(__file__).parent / "templates",
):
    app = Flask(__name__, static_folder=str(static_folder), template_folder=str(template_folder))
    app.config['DATASET'] = dataset
    app.config['EPISODES'] = episodes

    @app.route('/')
    def homepage():
        current_dataset = app.config['DATASET']
        if current_dataset is not None:
            return redirect(url_for(
                'show_first_episode',
                dataset_namespace=current_dataset.namespace,
                dataset_name=current_dataset.name
            ))
        return render_template('homepage.html', featured=["lerobot/panda_pick_coke_can"])

    @app.route('/<dataset_namespace>/<dataset_name>')
    def show_first_episode(dataset_namespace, dataset_name):
        dataset = app.config.get('DATASET')
        if dataset is None:
            try:
                dataset = LeRobotDataset(f"{dataset_namespace}/{dataset_name}")
                if dataset.version != "1.0":
                    return "Invalid dataset version", 400
            except Exception as e:
                return str(e), 404
        return redirect(url_for(
            'show_episode',
            dataset_namespace=dataset_namespace,
            dataset_name=dataset_name,
            episode_id=dataset.episodes[0]
        ))

    @app.route('/<dataset_namespace>/<dataset_name>/<int:episode_id>')
    def show_episode(dataset_namespace, dataset_name, episode_id):
        dataset = app.config.get('DATASET')
        allowed_episodes = app.config.get('EPISODES')

        if dataset is None:
            try:
                dataset = LeRobotDataset(f"{dataset_namespace}/{dataset_name}")
            except Exception as e:
                return str(e), 404
        else:
            if dataset.namespace != dataset_namespace or dataset.name != dataset_name:
                return "Invalid dataset", 400

        episodes_list = allowed_episodes if allowed_episodes is not None else dataset.episodes
        if episode_id not in episodes_list:
            return "Episode not found", 404

        index = episodes_list.index(episode_id)
        prev_id = episodes_list[index-1] if index > 0 else None
        next_id = episodes_list[index+1] if index < len(episodes_list)-1 else None

        return render_template(
            'episode.html',
            dataset_namespace=dataset_namespace,
            dataset_name=dataset_name,
            episode_id=episode_id,
            video_path=dataset.get_video_path(episode_id),
            task_description=dataset.get_task_description(episode_id),
            metadata=dataset.get_metadata(episode_id),
            prev_id=prev_id,
            next_id=next_id
        )

    app.run(host=host, port=port, debug=True)

