import asyncio
import torch
from typing import List

class ModelRunner:
    def __init__(self, model_name: str) -> None:
        self.model_name = model_name
        self.model = torch.jit.load(model_name)
        self.queue = []
        self.queue_lock = asyncio.Lock()
        self.needs_processing = asyncio.Event()
        self.needs_processing_timer = None
        self.batch_size = 4
        self.max_queue_size = 100
        self.wait_time = 0.1

    def schedule_processing_if_needed(self) -> None:
        if len(self.queue) >= self.batch_size:
            self.cancel_timer()
            self.needs_processing.set()
        elif len(self.queue) > 0:
            if self.needs_processing_timer is None:
                self.needs_processing_timer = asyncio.get_event_loop().call_later(
                    self.wait_time, self.trigger_processing
                )

    def trigger_processing(self):
        self.needs_processing_timer = None
        self.needs_processing.set()

    def cancel_timer(self):
        if self.needs_processing_timer is not None:
            self.needs_processing_timer.cancel()
            self.needs_processing_timer = None

    async def process_input(self, input: torch.Tensor) -> torch.Tensor:
        loop = asyncio.get_event_loop()
        future = loop.create_future()
        async with self.queue_lock:
            if len(self.queue) >= self.max_queue_size:
                raise RuntimeError("Queue limit exceeded")
            self.queue.append((input, future))
            self.schedule_processing_if_needed()
        return await future

    def run_model(self, batch: torch.Tensor) -> torch.Tensor:
        with torch.no_grad():
            return self.model(batch)

    async def model_runner(self) -> None:
        while True:
            await self.needs_processing.wait()
            self.cancel_timer()
            while True:
                batch_inputs = []
                batch_futures = []
                async with self.queue_lock:
                    if not self.queue:
                        break
                    items_to_process = self.queue[:self.batch_size]
                    self.queue = self.queue[self.batch_size:]
                for input_tensor, future in items_to_process:
                    batch_inputs.append(input_tensor)
                    batch_futures.append(future)
                try:
                    output_tensor = self.run_model(torch.stack(batch_inputs))
                    for i, future in enumerate(batch_futures):
                        future.set_result(output_tensor[i])
                except Exception as e:
                    for future in batch_futures:
                        future.set_exception(e)
            self.needs_processing.clear()

