        return WARequest.sendRequest(host, port, path, self.headers, self.params, "GET")

    def sendPostRequest(self):
        host, port, path = self.getConnectionParameters()
        return WARequest.sendRequest(host, port, path, self.headers, self.params, "POST")

    @staticmethod
    def sendRequest(host, port, path, headers, params, reqType="GET"):
        buffer = BytesIO()
        c = pycurl.Curl()
        c.setopt(c.WRITEDATA, buffer)
        if reqType == "GET":
            url = WARequest.build_get_url(host, path, params)
            c.setopt(c.URL, url)
        else:
            url = WARequest.build_get_url(host, path, [])
            c.setopt(c.URL, url)
            c.setopt(c.POST, 1)
            post_data = urlencode(params)
            c.setopt(c.POSTFIELDS, post_data)
        c.setopt(c.HTTPHEADER, WARequest.build_headers(headers))
        c.setopt(c.PORT, port)
        if port == 443:
            c.setopt(c.USE_SSL, pycurl.USESSL_DEFAULT)
        else:
            c.setopt(c.USE_SSL, pycurl.USESSL_NONE)
        c.perform()
        status_code = c.getinfo(c.RESPONSE_CODE)
        c.close()
        body = buffer.getvalue().decode('utf-8')
        return {'status': status_code, 'body': body}

    @staticmethod
    def sendRequestWithProxy(host, port, path, headers, params, proxy):
        buffer = BytesIO()
        c = pycurl.Curl()
        c.setopt(c.WRITEDATA, buffer)
        url = WARequest.build_get_url(host, path, params)
        c.setopt(c.URL, url)
        c.setopt(c.PROXY, proxy.host)
        c.setopt(c.PROXYPORT, proxy.port)
        c.setopt(c.HTTPHEADER, WARequest.build_headers(headers))
        c.setopt(c.PORT, port)
        c.perform()
        status_code = c.getinfo(c.RESPONSE_CODE)
        c.close()
        body = buffer.getvalue().decode('utf-8')
        return {'status': status_code, 'body': body}

    @staticmethod
    def build_get_url(host, path, params):
        query = urlencode(params) if params else ''
        return f'https://{host}/{path.lstrip("/")}?{query}' if query else f'https://{host}/{path.lstrip("/")}'

    @staticmethod
    def build_headers(headers_dict):
        return [f'{name}: {value}' for name, value in headers_dict.items()]

