    def __init__(self, prof_filename: str, start_browser: bool = False, port: Optional[int] = None):
        self.prof_filename = prof_filename
        self.start_browser = start_browser
        self.port = port
        self.data = self._read_profile()

    def _read_profile(self):
        try:
            return read_import_profile(self.prof_filename)
        except (TunaError, StopIteration):
            pass
        return read_runtime_profile(self.prof_filename)

    def _is_port_in_use(self, port: int) -> bool:
        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
            return s.connect_ex(("localhost", port)) == 0

    def start(self):
        if self.port is None:
            self.port = 8000
            while self._is_port_in_use(self.port):
                self.port += 1

        handler = lambda *args: StaticServer(self.data, self.prof_filename, *args)
        httpd = HTTPServer(("", self.port), handler)

        if self.start_browser:
            address = f"http://localhost:{self.port}"
            threading.Thread(target=lambda: webbrowser.open_new_tab(address)).start()

        print(f"Starting httpd on port {self.port}")
