import logging
from confluent_kafka import Producer
from confluent_kafka.admin import AdminClient, NewTopic

def kafka_producer(servers, topic, input_path, timeout):
    logging.basicConfig(level=logging.INFO)
    admin = AdminClient({'bootstrap.servers': servers})
    existing_topics = admin.list_topics().topics
    if topic not in existing_topics:
        logging.info(f"Creating topic {topic}")
        new_topic = NewTopic(topic, num_partitions=1, replication_factor=1)
        futures = admin.create_topics([new_topic])
        for future in futures:
            future.result()
    else:
        logging.info(f"Topic {topic} already exists")

    producer_conf = {
        'bootstrap.servers': servers,
        'request.timeout.ms': timeout * 1000
    }
    producer = Producer(producer_conf)
    try:
        with open(input_path, 'r') as f:
            for line_number, line in enumerate(f):
                stripped = line.strip()
                if stripped:
                    try:
                        producer.produce(topic, value=stripped.encode('utf-8'))
                        producer.poll(0)
                    except Exception as e:
                        logging.error(f"Error sending line {line_number + 1}: {e}")
        producer.flush()
    finally:
        producer.flush()

