            return
        self._closed = True
        self._unregister_cleanup()
        self.flush(timeout)
        self._sender.stop()

    def _cleanup_factory(self):
        def cleanup():
            if not self._closed:
                self.close()
        return cleanup

    def _unregister_cleanup(self):
        try:
            atexit.unregister(self._cleanup)
        except AttributeError:
            pass

