import socket
import queue
import threading
from urllib.parse import urlparse

class Timeout:
    DEFAULT_TIMEOUT = socket._GLOBAL_DEFAULT_TIMEOUT

class Retry:
    def __init__(self, total=3):
        self.total = total

    def is_retryable(self, exception):
        return isinstance(exception, (socket.timeout, ConnectionResetError))

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host.lower() if host else host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries if retries is not None else Retry()
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.proxy_host = None
        self.proxy_port = None
        if _proxy:
            parsed = urlparse(_proxy)
            self.proxy_host = parsed.hostname
            self.proxy_port = parsed.port or 80
            if parsed.scheme == 'https':
                self.scheme = 'https'
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.pool_lock = threading.Lock()
        self.headers = headers or {}

    def _new_conn(self):
        if self.proxy:
            return http.client.HTTPConnection(self.proxy_host, self.proxy_port, **self.conn_kw)
        return http.client.HTTPConnection(self.host, self.port, **self.conn_kw)

    def _make_request(self, conn, method, url, timeout=_Default, chunked=False, **httplib_request_kw):
        headers = httplib_request_kw.pop('headers', {})
        if timeout is not _Default:
            conn.sock.settimeout(timeout)
        parsed_url = urlparse(url)
        request_url = url if self.proxy else parsed_url.path or '/'
        if self.proxy and parsed_url.netloc:
            headers['Host'] = parsed_url.netloc
        if parsed_url.query and not self.proxy:
            request_url += '?' + parsed_url.query
        conn.request(method, request_url, headers=headers, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, chunked=False, **response_kw):
        retries = retries if retries is not None else self.retries
        redirect_count = 0
        while True:
            if redirect_count >= 3:
                raise Exception("Too many redirects")
            conn = self._get_conn(pool_timeout)
            try:
                response = self._make_request(conn, method, url, body=body, timeout=timeout, chunked=chunked, headers=headers)
                if redirect and response.status in (301, 302, 303, 307, 308):
                    redirect_count += 1
                    location = response.getheader('Location')
                    url = location
                    self._put_conn(conn)
                    continue
                return response
            except (socket.timeout, ConnectionError) as e:
                if retries.is_retryable(e):
                    retries = retries.decrement()
                    self._put_conn(conn)
                    continue
                else:
                    self._close_conn(conn)
                    raise
            finally:
                if release_conn:
                    self._put_conn(conn)

    def _get_conn(self, timeout):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put_nowait(conn)
        except queue.Full:
            conn.close()

    def _close_conn(self, conn):
        conn.close()

