import confluent_kafka
from confluent_kafka import Producer

def test_kafka_producer(kafka_broker, compression):
    if compression in ('lz4', 'zstd'):
        lib_version = confluent_kafka.libversion()[0]
        if compression == 'lz4' and lib_version < 0x00090100:
            pytest.skip('LZ4 compression requires librdkafka >= 0.9.1')
        if compression == 'zstd' and lib_version < 0x01050000:
            pytest.skip('Zstd compression requires librdkafka >= 1.5.0')

    conf = {
        'bootstrap.servers': f'{kafka_broker.host}:{kafka_broker.port}',
        'compression.type': compression,
    }
    producer = Producer(conf)

    topic = 'test_topic'
    num_messages = 10
    delivered = 0

    def delivery_report(err, msg):
        nonlocal delivered
        if err is None:
            delivered += 1

    for i in range(num_messages):
        producer.produce(topic, key=str(i), value=f'test_message_{i}', callback=delivery_report)

    producer.flush(timeout=10)
    producer.close()

    assert delivered == num_messages

