import subprocess

class SubprocessScanner:
    def __init__(self, host):
        self.host = host

    def useMasscanTCP(self):
        cmd = ['masscan', self.host, '-p0-65535', '--tcp']
        result = subprocess.run(cmd, capture_output=True, text=True)
        ports = []
        for line in result.stdout.splitlines():
            if line.startswith('Discovered open port'):
                parts = line.split()
                port_part = parts[3].split('/')[0]
                ports.append(int(port_part))
        return list(set(ports))

    def useMasscanUDP(self):
        cmd = ['masscan', self.host, '-p0-65535', '--udp']
        result = subprocess.run(cmd, capture_output=True, text=True)
        ports = []
        for line in result.stdout.splitlines():
            if line.startswith('Discovered open port'):
                parts = line.split()
                port_part = parts[3].split('/')[0]
                ports.append(int(port_part))
        return list(set(ports))

    def useNmapServTCP(self, port):
        cmd = ['nmap', '-sV', '-p', port, self.host]
        result = subprocess.run(cmd, capture_output=True, text=True)
        service_info = ''
        for line in result.stdout.splitlines():
            if line.startswith(f"{port}/tcp"):
                parts = line.split()
                if len(parts) >= 4:
                    service = parts[2]
                    version = ' '.join(parts[3:])
                    service_info = f"{service}: {version}"
                break
        return service_info

    def useNmapServUDP(self, port):
