import socket
import struct
import sys

PROXY_TYPE_SOCKS4 = 0x01
PROXY_TYPE_SOCKS5 = 0x02
PROXY_TYPE_HTTP = 0x03

class socksocket(socket.socket):
    default_proxy = None

    def __init__(self, family=socket.AF_INET, type=socket.SOCK_STREAM, proto=0, *args, **kwargs):
        super(socksocket, self).__init__(family, type, proto, *args, **kwargs)
        if family not in (socket.AF_INET, socket.AF_INET6):
            raise socket.error("Unsupported address family")
        if type not in (socket.SOCK_STREAM, socket.SOCK_DGRAM):
            raise socket.error("Unsupported socket type")
        self.proxy = None
        self._proxyconn = None
        self.proxy_sockname = None
        self.proxy_peername = None
        self._timeout = self.gettimeout()
        if self.default_proxy is not None:
            self.set_proxy(*self.default_proxy)

    def set_proxy(self, proxy_type, addr, port=None, rdns=True, username=None, password=None):
        self.proxy = (proxy_type, addr, port, rdns, username, password)

    def connect(self, dest_pair):
        dest_addr, dest_port = dest_pair
        if self.proxy is None:
            super(socksocket, self).connect((dest_addr, dest_port))
            self.proxy_peername = super(socksocket, self).getpeername()
            return
        proxy_type, addr, port, rdns, username, password = self.proxy
        if not port:
            port = 80 if proxy_type == PROXY_TYPE_HTTP else 1080
        if self.type == socket.SOCK_STREAM:
            super(socksocket, self).connect((addr, port))
        elif self.type == socket.SOCK_DGRAM:
            self._proxyconn = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self._proxyconn.settimeout(self._timeout)
            self._proxyconn.connect((addr, port))
        if proxy_type == PROXY_TYPE_SOCKS4:
            self._negotiate_SOCKS4(dest_addr, dest_port)
        elif proxy_type == PROXY_TYPE_SOCKS5:
            self._negotiate_SOCKS5(dest_addr, dest_port)
        elif proxy_type == PROXY_TYPE_HTTP:
            self._negotiate_HTTP(dest_addr, dest_port)
        self.proxy_peername = (dest_addr, dest_port)

    def _readall(self, file, count):
        data = b''
        while len(data) < count:
            s = file.read(count - len(data))
            if not s:
                raise socket.error("Connection closed unexpectedly")
            data += s
        return data

    def _proxy_addr(self):
        proxy_type, addr, port, rdns, username, password = self.proxy
        if not port:
            port = 80
        try:
            ip = socket.gethostbyname(addr)
        except socket.gaierror:
            raise socket.error("Could not resolve proxy address")
        return (ip, port)

    def _negotiate_SOCKS4(self, dest_addr, dest_port):
        import struct
        rdns = self.proxy[3]
        try:
            ip = socket.inet_aton(dest_addr)
        except socket.error:
            if rdns:
                ip = socket.inet_aton(socket.gethostbyname(dest_addr))
            else:
                raise socket.error("SOCKS4 requires IP address")
        req = struct.pack("!BBH4s", 4, 1, dest_port, ip) + b'\x00'
        self.sendall(req)
        resp = self._readall(self.makefile(), 8)
        if resp[1] != 0x5A:
            raise socket.error("SOCKS4 connection failed")

    def _negotiate_SOCKS5(self, dest_addr, dest_port):
        import struct
        username = self.proxy[4]
        password = self.proxy[5]
        methods = [0]
        if username and password:
            methods.append(2)
        self.sendall(struct.pack('!BBB', 5, len(methods), *methods))
        resp = self.recv(2)
        if len(resp) != 2 or resp[0] != 5:
            raise socket.error("SOCKS5 protocol error")
        method = resp[1]
        if method == 2:
            self.sendall(struct.pack('!BB', 1, len(username)) + username.encode() +
                         struct.pack('B', len(password)) + password.encode())
            resp = self.recv(2)
            if len(resp) != 2 or resp[0] != 1 or resp[1] != 0:
                raise socket.error("SOCKS5 authentication failed")
        elif method != 0:
            raise socket.error("No supported authentication method")
        try:
            ip = socket.inet_aton(dest_addr)
            atyp = 1
            addr = ip
        except socket.error:
            if self.proxy[3]:
                atyp = 3
                addr = dest_addr.encode()
            else:
                ip = socket.inet_aton(socket.gethostbyname(dest_addr))
                atyp = 1
                addr = ip
        if atyp == 3:
            req = struct.pack('!BBBB', 5, 1, 0, len(addr)) + addr
        else:
            req = struct.pack('!BBB', 5, 1, 0) + struct.pack('!B', atyp) + addr
        req += struct.pack('!H', dest_port)
        self.sendall(req)
        resp = self._readall(self.makefile(), 4)
        if len(resp) < 4 or resp[1] != 0:
            raise socket.error("SOCKS5 proxy error")
        atyp = resp[3]
        if atyp == 1:
            ip = self._readall(self.makefile(), 4)
        elif atyp == 3:
            len_byte = self._readall(self.makefile(), 1)
            length = ord(len_byte)
            ip = self._readall(self.makefile(), length)
        elif atyp == 4:
            ip = self._readall(self.makefile(), 16)
        else:
            raise socket.error("Unknown address type")
        port = self._readall(self.makefile(), 2)
        self.proxy_sockname = (socket.inet_ntoa(ip), struct.unpack('!H', port)[0])

    def _negotiate_HTTP(self, dest_addr, dest_port):
        import base64
        request = "CONNECT {}:{} HTTP/1.1\r\n".format(dest_addr, dest_port)
        self.sendall(request.encode())
        if self.proxy[4] and self.proxy[5]:
            auth = base64.b64encode(("{}:{}".format(self.proxy[4], self.proxy[5])).encode()).strip()
            self.sendall("Proxy-Authorization: Basic {}\r\n".format(auth.decode()).encode())
        self.sendall(b"\r\n")
        file = self.makefile()
        line = file.readline()
        if not line.startswith(b"HTTP/1.1 200"):
            while True:
                line = file.readline()
                if not line or line == b"\r\n":
                    break
            raise socket.error("HTTP proxy error: {}".format(line.strip().decode()))
        while True:
            line = file.readline()
            if not line or line == b"\r\n":
                break

