                },
                finish_reason=samba_response['result']['responses'][0]['stop_reason'],
            )
        ]

        return ChatCompletion.construct(
            id=None,
            choices=choices,
            created=int(time.time()),
            model=self.model_type,
            object="chat.completion",
            usage=CompletionUsage(
                completion_tokens=0,
                prompt_tokens=0,
                total_tokens=int(samba_response['result']['responses'][0]['total_tokens_count']),
            ),
        )

