    def __init__(self, env):
        self.env = env

    def parse_telnet(self, data):
        data = re.sub('([^\r])\r', '\\1', data)
        data = data.replace('\r\x00', '')
        data = data.replace('\x00', '')
        return data

    def raw_input(self, prompt=None):
        if prompt:
            self.write(prompt)

