                buf = self.parse_telnet(buf + data)
                if buf != '':
                    return buf
            elif data == '\004' or data == '':
                raise EOFError
            else:
                buf += data

    def write(self, data):
        self.server.socketio.write(data)
        self.server.socketio.flush()

