        if self._ping_interval and self.protocol and not self._closed:
            self._ping_callback = IOLoop.current().call_later(self._ping_interval, self._send_ping)

    def _send_ping(self):
        if self._closed:
            return
        self.ping(b"ping")
        self._ping_callback = IOLoop.current().call_later(self._ping_interval, self._send_ping)

    def _setup_pong_timeout(self):
        if self._ping_timeout:
            self._pong_callback = IOLoop.current().call_later(self._ping_timeout, self._on_pong_timeout)

    def _cancel_pong_timeout(self):
        if self._pong_callback:
            IOLoop.current().remove_timeout(self._pong_callback)
            self._pong_callback = None

    def _on_pong_timeout(self):
        if not self._closed:
            self.close(code=4500, reason="Pong timeout")

    def _cancel_ping(self):
        if self._ping_callback:
            IOLoop.current().remove_timeout(self._ping_callback)
            self._ping_callback = None
        self._cancel_pong_timeout()

