import base64
import os
import requests

def get_audio_transcript(ctx, sender, audiodata):
    try:
        decoded_audio = base64.b64decode(audiodata)
    except Exception as e:
        ctx.send(sender, Error(f"Failed to decode base64 audio data: {e}"))
        return

    api_token = os.environ.get("HUGGINGFACE_API_TOKEN")
    if not api_token:
        ctx.send(sender, Error("HuggingFace API token not found in environment variables."))
        return

    headers = {"Authorization": f"Bearer {api_token}"}
    files = {"file": ("audio.wav", decoded_audio, "audio/wav")}
    endpoint = "https://api-inference.huggingface.co/models/openai/whisper-large-v2"

    try:
        response = requests.post(endpoint, headers=headers, files=files)
        response.raise_for_status()
    except requests.exceptions.RequestException as e:
        ctx.send(sender, Error(f"API request failed: {e}"))
        return

    try:
        transcript = response.json()["text"]
    except KeyError:
        ctx.send(sender, Error("Transcription not found in API response."))
        return
    except Exception as e:
        ctx.send(sender, Error(f"Error parsing API response: {e}"))
        return

    ctx.send(sender, AudioTranscriptResponse(transcript))

