        self.username = username
        self.password = password
        self.verify_ssl = verify_ssl
        self.scheme = "https"
        self.kibana_url = f"{self.scheme}://localhost:5601"
        self.es_url = f"{self.scheme}://localhost:9200"
        self.headers = {"kbn-xsrf": "true"}

    def fetch_kibana_object(self, obj_type):
        try:
            response = requests.post(
                f"{self.kibana_url}/api/saved_objects/_export",
                json={"type": obj_type},
                verify=self.verify_ssl,
                auth=(self.username, self.password),
                headers=self.headers,
            )
            if response.status_code != 200:
                raise Exception(f"HTTP status code {response.status_code}")
            return ndjson.loads(response.text.encode("utf-8"))
        except Exception as error:
            raise Exception(f"Error fetching kibana object {obj_type}: {error}")

    def fetch_es_template(self, template_name):
        try:
            response = requests.get(
                f"{self.es_url}/_template/{template_name}",
                verify=self.verify_ssl,
                auth=(self.username, self.password),
            )
            if response.status_code != 200:
                raise Exception(f"HTTP status code {response.status_code}")
            return json.loads(response.text.encode("utf-8"))
        except Exception as error:
            raise Exception(f"Error fetching ES template {template_name}: {error}")

