import requests
import os
from tqdm import tqdm

def download_market_data(exchange, data_type, symbol, yyyymmdd, filepath, key):
    date_str = str(yyyymmdd)
    url = f"https://api.example.com/v1/market_data/{exchange}/{data_type}/{symbol}/{date_str}"
    headers = {"Authorization": f"Bearer {key}"}
    os.makedirs(os.path.dirname(filepath), exist_ok=True)

    try:
        with requests.get(url, headers=headers, stream=True) as response:
            response.raise_for_status()
            total_size = int(response.headers.get('Content-Length', 0))
            downloaded = 0

            with tqdm(total=total_size, unit='B', unit_scale=True, desc='Downloading') as pbar:
                with open(filepath, 'wb') as f:
                    for chunk in response.iter_content(chunk_size=8192):
                        if chunk:
                            f.write(chunk)
                            chunk_size = len(chunk)
                            downloaded += chunk_size
                            pbar.update(chunk_size)

            if total_size != 0 and downloaded != total_size:
                os.remove(filepath)
                raise RuntimeError("Incomplete download: size mismatch")

    except requests.HTTPError as e:
        raise RuntimeError(f"HTTP error {response.status_code}") from e
    except requests.RequestException as e:
        raise RuntimeError(f"Request failed: {e}") from e
    except Exception as e:
        if os.path.exists(filepath):
            os.remove(filepath)
        raise RuntimeError(f"Download failed: {e}") from e

