        encoded_path = requests.utils.quote(destination_path)
        upload_url = f"{self.FIREBASE_API_URL}/b/{self.FIREBASE_STORAGE_BUCKET}/o?uploadType=media&name={encoded_path}&{self.API_KEY_PARAM}"
