import numpy as np
import requests
from openai import OpenAI

def get_embedding_from_api(word, model="vicuna-7b-v1.1"):
    if "ada" in model.lower():
        client = OpenAI()
        try:
            response = client.embeddings.create(input=word, model=model)
            return np.array(response.data[0].embedding)
        except Exception as e:
            print(f"OpenAI API error: {e}")
            return None
    else:
        api_url = "http://localhost:8000/embedding"
        payload = {"text": word, "model_name": model}
        try:
            response = requests.post(api_url, json=payload)
            response.raise_for_status()
            embedding_list = response.json()["embedding"]
            return np.array(embedding_list)
        except requests.exceptions.RequestException as e:
            print(f"Local API error: {e}")
            return None
        except KeyError:
            print("Response does not contain 'embedding' key")
            return None

