class FlaskWebApp:
    def __init__(self, redis_conn):
        self.app = Flask(__name__)
        self.app.config['PROPAGATE_EXCEPTIONS'] = True
        self.redis_conn = redis_conn
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route("/")
        def index():
            return render_template('index.html'), 200

