    def _reset_retries(self):
        self._tries = 0
        self._delay = 1

    def _get_proxy_config(self):
        proxy_server = None
        proxy_port = None
        proxy_username = None
        proxy_password = None
        proxy_auth = None
        ssl_enabled = self._ssl_enabled

