        self.graphiql = graphiql
        self.graphql_ide = graphql_ide
        self.allow_queries_via_get = allow_queries_via_get
        self.result_override = result_override
        self.multipart_uploads_enabled = multipart_uploads_enabled

    async def _graphql_request(self, method: Literal["get", "post"], query: Optional[str] = None, variables: Optional[Dict[str, object]] = None, files: Optional[Dict[str, BytesIO]] = None, headers: Optional[Dict[str, str]] = None, **kwargs: Any) -> Response:
        if self.result_override is not None:
            return self.result_override

        body = {}
        if query:
            body["query"] = query
        if variables:
            body["variables"] = variables
        if files and self.multipart_uploads_enabled:
            form_data = []
            for name, file in files.items():
                form_data.append((name, file.getvalue(), 'application/octet-stream'))
            body["file"] = form_data

        url = "/graphql"
        if method == "get" and self.allow_queries_via_get:
            params = {k: v for k, v in body.items() if k != "file"}
            return await self.get(url, headers=headers, params=params)
        elif method == "post":
            return await self.post(url, json=body, headers=headers)
        else:
            raise ValueError(f"Unsupported method {method} for GraphQL request")

    async def request(self, url: str, method: Literal["get", "post", "patch", "put", "delete"], headers: Optional[Dict[str, str]] = None, **kwargs: Any) -> Response:
        async with self.app.test_client as client:
            response = await getattr(client, method)(url, headers=headers, **kwargs)
            return response

    async def get(self, url: str, headers: Optional[Dict[str, str]] = None, params: Optional[Dict[str, str]] = None) -> Response:
        kwargs = {}
        if params:
            kwargs["params"] = params
        return await self.request(url, "get", headers=headers, **kwargs)

    async def post(self, url: str, data: Optional[bytes] = None, json: Optional[Dict] = None, headers: Optional[Dict[str, str]] = None) -> Response:
        kwargs = {}
        if data is not None:
            kwargs["data"] = data
        if json is not None:
            kwargs["json"] = json
        return await self.request(url, "post", headers=headers, **kwargs)

