class KafkaProducer:
    def __init__(self, **configs):
        self.config = {
            'bootstrap_servers': 'localhost:9092',
            'key_serializer': lambda k: k.encode('utf-8') if isinstance(k, str) else k,
            'value_serializer': lambda v: v.encode('utf-8') if isinstance(v, str) else v,
            'acks': 'all',
            'retries': 5,
            'retry_backoff_ms': 1000,
            'max_in_flight_requests_per_connection': 5,
            'partitioner': 'round_robin',
            'compression_type': None,
            'max_request_size': 1048576,
            'message_timeout_ms': 300000,
            'metadata_max_age_ms': 300000
        }
        self.config.update(configs)
        self._metrics = Metrics()
        self._accumulator = RecordAccumulator()
        self._metadata = ClusterMetadata(self.config['bootstrap_servers'])
        self._sender = Sender(self.config, self._accumulator, self._metadata)
        self._closed = False
        self._cleanup = self._cleanup_factory()

    def send(self, topic, value=None, key=None, headers=None, partition=None, timestamp_ms=None):
        if self._closed:
            raise RuntimeError("Producer is closed")
        self._wait_on_metadata(topic, self.config['message_timeout_ms'])
        serialized_key = self._serialize(self.config['key_serializer'], topic, key)
        serialized_value = self._serialize(self.config['value_serializer'], topic, value)
        size = self._estimate_size_in_bytes(key, value, headers or [])
        self._ensure_valid_record_size(size)
        target_partition = self._partition(topic, partition, key, value, serialized_key, serialized_value)
        future = self._accumulator.append(topic, target_partition, timestamp_ms, serialized_key, serialized_value, headers)
        return future

    def flush(self, timeout=None):
        self._accumulator.flush()
        self._sender.wakeup()
        # Simulate waiting for all sends to complete within timeout
        pass

    def close(self, timeout=None):
        if not self._closed:
            self._closed = True
            self._unregister_cleanup()
            self.flush(timeout)
            self._sender.close(timeout)
            self._metadata.close()

    def _cleanup_factory(self):
        def cleanup():
            if not self._closed:
                self.close()
        import atexit
        atexit.register(cleanup)
        return cleanup

    def _unregister_cleanup(self):
        import atexit
        atexit.unregister(self._cleanup)

    def _max_usable_produce_magic(self):
        # Simplified logic for determining message format version
        api_version = self._metadata.get_api_version()
        if api_version >= (2, 1, 0):  # Kafka 2.1 introduced magic byte 2
            return 2
        elif api_version >= (0, 11, 0):  # Magic byte 1 for versions >= 0.11.0
            return 1
        else:
            return 0

    def _estimate_size_in_bytes(self, key, value, headers):
        size = 0
        if isinstance(key, str):
            size += len(key.encode('utf-8'))
        elif isinstance(key, bytes):
            size += len(key)
        if isinstance(value, str):
            size += len(value.encode('utf-8'))
        elif isinstance(value, bytes):
            size += len(value)
        for header in headers:
            if isinstance(header[0], str):
                size += len(header[0].encode('utf-8'))
            if isinstance(header[1], str):
                size += len(header[1].encode('utf-8'))
            elif isinstance(header[1], bytes):
                size += len(header[1])
        return size

    def _ensure_valid_record_size(self, size):
        max_size = self.config['max_request_size']
        if size > max_size:
            raise ValueError(f"Record size {size} exceeds maximum allowed size {max_size}")

    def _wait_on_metadata(self, topic, max_wait):
        return self._metadata.wait_for_update(topic, max_wait)

    def _serialize(self, f, topic, data):
        try:
            return f(data) if f else data
        except Exception as e:
            raise SerializationError(f"Failed to serialize {data} for topic {topic}") from e

    def _partition(self, topic, partition, key, value, serialized_key, serialized_value):
        available_partitions = self._metadata.get_partitions(topic)
        if partition is not None:
            if partition < 0 or partition >= len(available_partitions):
                raise ValueError(f"Invalid partition {partition} for topic {topic}")
            return partition
        if self.config['partitioner'] == 'round_robin':
            return (hash(key) % len(available_partitions)) if key else (hash(value) % len(available_partitions))
        elif self.config['partitioner'] == 'default':
            # Default Kafka partitioning logic using murmur2 hash of key
            return abs(hash(serialized_key)) % len(available_partitions) if serialized_key else -1
        else:
            raise ValueError(f"Unknown partitioner {self.config['partitioner']}")

