def test_kafka_producer_with_compression(kafka_broker, compression):
    broker = f"{kafka_broker.host}:{kafka_broker.port}"
    topic = "test-compression-topic"
    msg_count = 100
    conf = {
        'bootstrap.servers': broker,
        'message.timeout.ms': 10000,
        'enable.idempotence': True,
        'compression.type': compression
    }

    if compression in ['lz4', 'zstd']:
        admin_client = AdminClient({'bootstrap.servers': broker})
        cluster_metadata = admin_client.list_topics(timeout=10)
        broker_version = cluster_metadata.orig_broker.version

        if compression == 'lz4' and broker_version < (0, 8, 2):
            pytest.skip("LZ4 compression requires Kafka >= 0.8.2")
        elif compression == 'zstd' and broker_version < (2, 1, 0):
            pytest.skip("Zstd compression requires Kafka >= 2.1.0")

    try:
        producer = Producer(conf)
    except KafkaException as e:
        raise RuntimeError(f"Failed to create Kafka producer: {e.args[0].str()}")

    try:
        admin_client = AdminClient({'bootstrap.servers': broker})
        topic_metadata = admin_client.list_topics(timeout=10).topics
        if topic not in topic_metadata:
            new_topic = NewTopic(topic, num_partitions=1, replication_factor=1)
            fs = admin_client.create_topics([new_topic])
            for f in fs.values():
                f.result()
    except KafkaException as e:
        raise RuntimeError(f"Failed to verify or create topic: {e.args[0].str()}")

    delivered = 0

    def delivery_report(err, msg):
        nonlocal delivered
        if err:
            raise KafkaException(err)
        delivered += 1

    try:
        for i in range(msg_count):
            producer.produce(topic, key=str(i), value=f"Message #{i}", callback=delivery_report)
        producer.poll(1)
        producer.flush(timeout=10)
    except KafkaException as e:
        raise RuntimeError(f"Message delivery failed: {e.args[0].str()}")
    finally:
        producer = None

    assert delivered == msg_count, f"Expected {msg_count} messages, but only {delivered} were delivered"

