class SubprocessScanner:
    def __init__(self, host):
        self.host = host

    def useMasscanTCP(self):
        try:
            result = subprocess.run(
                ['masscan', self.host, '-p1-65535', '--rate=1000', '--proto=tcp'],
                stdout=subprocess.PIPE,
                stderr=subprocess.PIPE,
                text=True,
                check=True
            )
            return self._parse_masscan_output(result.stdout, 'tcp')
        except subprocess.CalledProcessError:
            return []

    def useMasscanUDP(self):
        try:
            result = subprocess.run(
                ['masscan', self.host, '-p1-65535', '--rate=1000', '--proto=udp'],
                stdout=subprocess.PIPE,
                stderr=subprocess.PIPE,
                text=True,
                check=True
            )
            return self._parse_masscan_output(result.stdout, 'udp')
        except subprocess.CalledProcessError:
            return []

    def useNmapServTCP(self, port):
        try:
            result = subprocess.run(
                ['nmap', '-sV', '-p', f'{port}/tcp', self.host],
                stdout=subprocess.PIPE,
                stderr=subprocess.PIPE,
                text=True,
                check=True
            )
            return self._parse_nmap_output(result.stdout)
        except subprocess.CalledProcessError:
            return ""

    def useNmapServUDP(self, port):
        try:
            result = subprocess.run(
                ['nmap', '-sV', '-p', f'{port}/udp', self.host],
                stdout=subprocess.PIPE,
                stderr=subprocess.PIPE,
                text=True,
                check=True
            )
            return self._parse_nmap_output(result.stdout)
        except subprocess.CalledProcessError:
            return ""

    def _parse_masscan_output(self, output, proto):
        ports = []
        pattern = rf'port {proto}/(\d+)'
        for line in output.splitlines():
            match = re.search(pattern, line)
            if match:
                ports.append(match.group(1))
        return ports

    def _parse_nmap_output(self, output):
        for line in output.splitlines():
            if 'service' in line.lower() and 'version' in line.lower():
                return line.strip()
        return ""

